import {useState, useEffect} from 'react';
import Axios from "axios"
import Pagination from '../components/Pagination';

const CustomersPageWithPagination = (props) => {

    const [customers, setCustomers] = useState([])

    // pour la pagination 
    const [currentPage, setCurrentPage] = useState(1)
    const [totalItems, setTotalItems] = useState(0)
    // dÃ©finir le nombre d'items par page
    const itemsPerPage = 10

    const handlePageChange = (page) => {
        setCustomers([])
        setCurrentPage(page)
    }

    useEffect(()=>{
        Axios.get(`http://127.0.0.1:8000/api/customers?pagination=true&count=${itemsPerPage}&page=${currentPage}`)
            .then(response => {
                setCustomers(response.data['hydra:member'])
                setTotalItems(response.data['hydra:totalItems'])
            })
    },[currentPage])

    const handleDelete = (id) => {
        // pessimiste
        const originalCustomers = [...customers]

        // optimiste
        setCustomers(customers.filter(customer => customer.id !== id))

        Axios.delete(`http://127.0.0.1:8000/api/customers/${id}`)
            .then(response => console.log('ok'))
            .catch(error => {
                setCustomers(originalCustomers)
                console.log(error.response)
            })
    }

    return ( 
        <>
             <h1>Liste des clients</h1>
            <table className='table table-hover'>
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Client</th>
                        <th>Email</th>
                        <th>Entreprise</th>
                        <th>Factures</th>
                        <th className='text-center'>Montant total</th>
                        <th className='text-center'>Montant restant</th>
                        <th></th>
                    </tr>    
                </thead> 
                <tbody>
                    {/* ET logique (&&) expr1 && expr2 renvoie expr1 si cette expression peut Ãªtre convertie en false, sinon renvoie expre 2 */}

                    {customers.length === 0 && (
                        <tr>
                            <td colSpan="8" className='text-center'>Chargement ...</td>
                        </tr>
                    )}

                    {customers.map(customer => (
                        <tr key={customer.id}>
                            <td>{customer.id}</td>
                            <td>{customer.firstName} {customer.lastName}</td>
                            <td>{customer.email}</td>
                            <td>{customer.company}</td>
                            <td className='text-center'>
                                <span className="badge badge-primary bg-primary">
                                    {customer.invoices.length}
                                </span>
                            </td>
                            <td className="text-center">{customer.totalAmount.toLocaleString()}â¬</td>
                            <td className="text-center">{customer.unpaidAmount.toLocaleString()}â¬</td>
                            <td>
                                <button
                                    disabled={customer.invoices.length > 0}
                                    onClick={()=> handleDelete(customer.id)} 
                                    className="btn btn-sm btn-danger">Supprimer</button>
                            </td>
                        </tr>
                    ))}
                </tbody>           
            </table>
            <Pagination 
                currentPage={currentPage}
                itemsPerPage={itemsPerPage}
                length={totalItems}
                onPageChanged={handlePageChange}
            />    

        </>

     );
}

export default CustomersPageWithPagination;
